import 'package:flutter/material.dart';import 'package:pdf_flutter_app/annotations.dart';import 'package:syncfusion_flutter_pdfviewer/pdfviewer.dart';class OverLayTest extends StatefulWidget {  @override  _OverLayTestState createState() => _OverLayTestState();}class _OverLayTestState extends State<OverLayTest> { // List<Offset> _points = <Offset>[];  @override  void initState() {    print(GlobalData.points);    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      floatingActionButton: FloatingActionButton(          child: Icon(Icons.close),          onPressed: (){        GlobalData.points.clear();      }),      body: GestureDetector(        onPanUpdate: (DragUpdateDetails details) {          setState(() {            RenderBox object = context.findRenderObject();            Offset _localPosition =            object.globalToLocal(details.globalPosition);            print('_localPosition---');            print(_localPosition);            GlobalData.points = new List.from(GlobalData.points)..add(_localPosition);            print('_points---------');            print(GlobalData.points);          });        },        onPanEnd: (DragEndDetails details) {          GlobalData.points.add(null);          print('end-----------');           //_points.clear();        },        child: Stack(          children: [            SfPdfViewer.asset('assets/aeen101.pdf',            canShowScrollHead: true,              enableDoubleTapZooming: true,            ),            CustomPaint(              painter:  Signature(points: GlobalData.points),              size: Size.infinite,            ),          ],        ),),    );  }}class GlobalData{  static List<Offset>points=[];}